step.reason:
since.
\vert x \vert = \begin{cases} 
           	-x, if x\lt 0\\
	 x, if x\ge0\\
         	\end{cases}

step.tex:
\begin{align}
    \lim_{x\to -3^-}f(x) &= \lim_{x\to -3^-} (-x+3) =6\\
    \lim_{x\to -3^+}f(x) &= \lim_{x\to -3^+} (-2x) =6 \\
    \f(-3) = -(-3)+3 = 6\\
    \lim_{x\to 3^-}f(x) &= \lim_{x\to 3^-} (-2x)  =-6\\
    \lim_{x\to 3^+}f(x) &= \lim_{x\to-3^+} (6x+2)=20 \\
    f(3) = (6(3)+2) = 20\\
\end{align}
\\ \text{Hence, f(x) is discontinuous at x=3.}\\
\therefore \text{x=3 is the only point of discontinuity.}

\begin{align}
    \lim_{x\to -3^-}f(x) &= \lim_{x\to -3^-} (-x+3)  = 6\\
    \lim_{x\to -3^+}f(x) &= \lim_{x\to -3^+} (-2x)  = -6 \\
    f(-3) = -(-3)+3 = 6\\
    \lim_{x\to 3^-}f(x) &= \lim_{x\to 3^-} (-2x)  = -6\\
    \lim_{x\to 3^+}f(x) &= \lim_{x\to-3^+} (6x+2)  = 20 \\
    f(3) = (6(3)+2) = 20\\
\end{align}
\\ \text{Hence, f(x) is discontinuous at x=3,-3.}\\
\therefore \text{x=3,-3 are the points of discontinuity.}
